import React from 'react'
import { mdx } from '@mdx-js/react'

/* @jsx mdx */



const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1>{`Quick Start`}</h1>
    <p>{`Note：`}</p>
    <p><strong parentName="p">{`node >= 10.13.0`}</strong></p>
    <p><strong parentName="p">{`node >= 10.13.0`}</strong></p>
    <p><strong parentName="p">{`node >= 10.13.0`}</strong></p>
    <h2>{`Initialize a project`}</h2>
    <ul>
      <li parentName="ul">
        <p parentName="li">{`One method is use npx：`}</p>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-shell"
          }}>{`npx @omni-door/cli init -s myProject
`}</code></pre>
      </li>
      <li parentName="ul">
        <p parentName="li">{`Another you can install cli to the global：`}</p>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-shell"
          }}>{`npm i -g @omni-door/cli
`}</code></pre>
        <p parentName="li">{`  Then Perform initialization after installation：`}</p>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-shell"
          }}>{`omni init -s myProject
`}</code></pre>
        <blockquote parentName="li">
          <p parentName="blockquote">{`note: The project name is `}<inlineCode parentName="p">{`myProject`}</inlineCode></p>
        </blockquote>
      </li>
    </ul>
    <h2>{`Run project`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-shell"
      }}>{`npm run dev
`}</code></pre>
    <h2>{`Create a Component`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-shell"
      }}>{`npm run new
`}</code></pre>
    <h2>{`Build project`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-shell"
      }}>{`npm run build
`}</code></pre>
    <h2>{`Release project`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-shell"
      }}>{`npm run release
`}</code></pre>
    </MDXLayout>;
}

;
MDXContent.isMDXComponent = true;
