import { mdx } from '@mdx-js/react'

/* @jsx mdx */



const layoutProps = {

};
const MDXLayout = "wrapper"
export default function MDXContent({
  components,
  ...props
}) {
  return <MDXLayout {...layoutProps} {...props} components={components} mdxType="MDXLayout">
    <h1>{`快速开始`}</h1>
    <p>{`说三遍：`}</p>
    <p><strong parentName="p">{`node >= 10.13.0`}</strong></p>
    <p><strong parentName="p">{`node >= 10.13.0`}</strong></p>
    <p><strong parentName="p">{`node >= 10.13.0`}</strong></p>
    <h2>{`初始化一个项目`}</h2>
    <ol>
      <li parentName="ol">
        <p parentName="li">{`可以使用 `}<inlineCode parentName="p">{`npx`}</inlineCode>{` 来初始化项目：`}</p>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-shell"
          }}>{`npx @omni-door/cli init -s myProject
`}</code></pre>
      </li>
      <li parentName="ol">
        <p parentName="li">{`若要创建多个项目，把脚手架安装到全局：`}</p>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-shell"
          }}>{`npm i -g @omni-door/cli
`}</code></pre>
        <p parentName="li">{`  安装完成后执行初始化操作：`}</p>
        <pre parentName="li"><code parentName="pre" {...{
            "className": "language-shell"
          }}>{`omni init -s myProject
`}</code></pre>
        <blockquote parentName="li">
          <p parentName="blockquote">{`note: 项目名称为 `}<inlineCode parentName="p">{`myProject`}</inlineCode></p>
        </blockquote>
      </li>
    </ol>
    <h2>{`运行开发环境`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-shell"
      }}>{`npm run dev
`}</code></pre>
    <h2>{`新建一个组件`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-shell"
      }}>{`npm run new
`}</code></pre>
    <h2>{`构建项目`}</h2>
    <pre><code parentName="pre" {...{
        "className": "language-shell"
      }}>{`npm run build
`}</code></pre>
    </MDXLayout>;
}

;
